        </div>
        <div class="group-actions">
          <button class="btn btn-primary btn-small apply-btn" data-index="${index}">é©ç”¨</button>
          <button class="btn btn-secondary btn-small edit-btn" data-index="${index}">ç·¨é›†</button>
          <button class="btn btn-danger btn-small delete-btn" data-index="${index}">å‰Šé™¤</button>
        </div>
      </div>
      ${isExpanded ? `
        <div class="group-members-list">
          ${group.members.map(member => `
            <div class="member-item">
              <svg width="12" height="12" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
                <circle cx="12" cy="7" r="4"></circle>
