    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);

    showMessage('è¨­å®šã‚’ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆã—ã¾ã—ãŸ', 'success');
