}

// ã‚«ãƒ¬ãƒ³ãƒ€ãƒ¼ãƒªã‚¹ãƒˆã®èª­ã¿è¾¼ã¿
async function loadCalendars() {
  try {
    // content.jsã‹ã‚‰ã‚«ãƒ¬ãƒ³ãƒ€ãƒ¼ãƒªã‚¹ãƒˆã‚’å–å¾—
    const tabs = await chrome.tabs.query({active: true, currentWindow: true});
