    copyButton.style.display = 'block';

  } catch (error) {
    const errorInfo = `ã‚¨ãƒ©ãƒ¼: ${error.message}\n\nGoogleã‚«ãƒ¬ãƒ³ãƒ€ãƒ¼ã®ãƒšãƒ¼ã‚¸ã‚’ãƒªãƒ­ãƒ¼ãƒ‰ã—ã¦ã‹ã‚‰å†åº¦ãŠè©¦ã—ãã ã•ã„ã€‚`;
    debugDiv.innerHTML = errorInfo;
    lastDebugInfo = errorInfo;
    copyButton.style.display = 'block';
  }
}

// ãƒ‡ãƒãƒƒã‚°æƒ…å ±ã‚’ã‚¯ãƒªãƒƒãƒ—ãƒœãƒ¼ãƒ‰ã«ã‚³ãƒ”ãƒ¼
async function copyDebugInfo() {
  try {
    await navigator.clipboard.writeText(lastDebugInfo);
    showMessage('ãƒ‡ãƒãƒƒã‚°æƒ…å ±ã‚’ã‚³ãƒ”ãƒ¼ã—ã¾ã—ãŸ', 'success');
  } catch (error) {
    // ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯: ãƒ†ã‚­ã‚¹ãƒˆã‚¨ãƒªã‚¢ã‚’ä½¿ç”¨
    const textarea = document.createElement('textarea');
    textarea.value = lastDebugInfo;
    textarea.style.position = 'fixed';
    textarea.style.opacity = '0';
    document.body.appendChild(textarea);
    textarea.select();

    try {
      document.execCommand('copy');
      showMessage('ãƒ‡ãƒãƒƒã‚°æƒ…å ±ã‚’ã‚³ãƒ”ãƒ¼ã—ã¾ã—ãŸ', 'success');
    } catch (e) {
      showMessage('ã‚³ãƒ”ãƒ¼ã«å¤±æ•—ã—ã¾ã—ãŸ', 'error');
    }

    document.body.removeChild(textarea);
  }
}

