  padding-top: 16px;
  border-top: 1px solid #e0e0e0;
}

.backup-section h3 {
  font-size: 14px;
  font-weight: 600;
  color: #3c4043;
  margin-bottom: 12px;
}

.debug-info {
  background: #f0f0f0;
  border: 1px solid #ddd;
  border-radius: 4px;
  padding: 8px;
  margin-top: 8px;
  font-size: 12px;
  max-height: 250px;
