  // ãƒœã‚¿ãƒ³ã¨ã‚¿ã‚¤ãƒˆãƒ«ã‚’å¤‰æ›´
  document.getElementById('createGroup').textContent = 'ã‚°ãƒ«ãƒ¼ãƒ—æ›´æ–°';
  document.querySelector('.create-section h3').textContent = 'ã‚°ãƒ«ãƒ¼ãƒ—ç·¨é›†';
  document.getElementById('cancelEdit').style.display = 'inline-block';

  // ä½œæˆã‚»ã‚¯ã‚·ãƒ§ãƒ³ã¾ã§ã‚¹ã‚¯ãƒ­ãƒ¼ãƒ«
  document.querySelector('.create-section').scrollIntoView({ behavior: 'smooth' });

