    showMessage('ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆã«å¤±æ•—ã—ã¾ã—ãŸ', 'error');
  }
}

// è¨­å®šã®ã‚¤ãƒ³ãƒãƒ¼ãƒˆ
async function importSettings(event) {
  const file = event.target.files[0];
  if (!file) return;

  try {
    const text = await file.text();
    const importData = JSON.parse(text);

    // ãƒ‡ãƒ¼ã‚¿ã®æ¤œè¨¼
    if (!importData.version || !importData.groups || !Array.isArray(importData.groups)) {
      throw new Error('ç„¡åŠ¹ãªãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ãƒ•ã‚¡ã‚¤ãƒ«ã§ã™');
    }

    // ç¢ºèªãƒ€ã‚¤ã‚¢ãƒ­ã‚°
    const groupCount = importData.groups.length;
    const confirmMessage = `${groupCount}å€‹ã®ã‚°ãƒ«ãƒ¼ãƒ—ã‚’ã‚¤ãƒ³ãƒãƒ¼ãƒˆã—ã¾ã™ã€‚\nç¾åœ¨ã®è¨­å®šã¯ä¸Šæ›¸ãã•ã‚Œã¾ã™ã€‚ç¶šè¡Œã—ã¾ã™ã‹ï¼Ÿ`;

    if (!confirm(confirmMessage)) {
      event.target.value = ''; // ãƒ•ã‚¡ã‚¤ãƒ«é¸æŠã‚’ãƒªã‚»ãƒƒãƒˆ
      return;
    }

    // ã‚°ãƒ«ãƒ¼ãƒ—ã‚’ã‚¤ãƒ³ãƒãƒ¼ãƒˆ
    await chrome.storage.local.set({ calendarGroups: importData.groups });
    groups = importData.groups;

    // UIã‚’æ›´æ–°
    renderGroupList();
    showMessage(`${groupCount}å€‹ã®ã‚°ãƒ«ãƒ¼ãƒ—ã‚’ã‚¤ãƒ³ãƒãƒ¼ãƒˆã—ã¾ã—ãŸ`, 'success');

    // ãƒ•ã‚¡ã‚¤ãƒ«é¸æŠã‚’ãƒªã‚»ãƒƒãƒˆ
    event.target.value = '';
  } catch (error) {
    console.error('ã‚¤ãƒ³ãƒãƒ¼ãƒˆã‚¨ãƒ©ãƒ¼:', error);
    showMessage('ã‚¤ãƒ³ãƒãƒ¼ãƒˆã«å¤±æ•—ã—ã¾ã—ãŸ: ' + error.message, 'error');
    event.target.value = ''; // ãƒ•ã‚¡ã‚¤ãƒ«é¸æŠã‚’ãƒªã‚»ãƒƒãƒˆ
  }
}

