// ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã‚’è¡¨ç¤º
async function showErrorLogs() {
  const errorLogsDiv = document.getElementById('errorLogs');
  const clearButton = document.getElementById('clearErrorLogs');
  const showButton = document.getElementById('showErrorLogs');

  // ãƒˆã‚°ãƒ«å‹•ä½œ
  if (errorLogsDiv.style.display !== 'none') {
    errorLogsDiv.style.display = 'none';
    clearButton.style.display = 'none';
    showButton.textContent = 'ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã‚’è¡¨ç¤º';
    return;
  }

  try {
    const result = await chrome.storage.local.get(['errorLogs']);
    const errorLogs = result.errorLogs || [];

    if (errorLogs.length === 0) {
      errorLogsDiv.innerHTML = '<div style="text-align: center; color: #666;">ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã¯ã‚ã‚Šã¾ã›ã‚“</div>';
    } else {
      const logsHtml = errorLogs.map(log => {
        const date = new Date(log.timestamp);
        const timeStr = date.toLocaleString('ja-JP');

        return `
          <div class="error-log-item">
            <div class="error-log-time">${timeStr}</div>
            <div class="error-log-context">[${log.context}]</div>
            <div class="error-log-message">${escapeHtml(log.message)}</div>
            ${log.stack ? `<div class="error-log-stack">${escapeHtml(log.stack)}</div>` : ''}
          </div>
        `;
      }).join('');

      errorLogsDiv.innerHTML = logsHtml;
    }

    errorLogsDiv.style.display = 'block';
    clearButton.style.display = 'block';
    showButton.textContent = 'ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã‚’éš ã™';
  } catch (error) {
    console.error('ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã®èª­ã¿è¾¼ã¿ã«å¤±æ•—:', error);
    ErrorLogger.saveError(error, 'popup-show-error-logs');
    showMessage('ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã®èª­ã¿è¾¼ã¿ã«å¤±æ•—ã—ã¾ã—ãŸ', 'error');
  }
}

// ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã‚’ã‚¯ãƒªã‚¢
async function clearErrorLogs() {
  if (!confirm('ã™ã¹ã¦ã®ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã‚’å‰Šé™¤ã—ã¾ã™ã‹ï¼Ÿ')) {
    return;
  }

  try {
    await chrome.storage.local.set({ errorLogs: [] });
    showMessage('ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã‚’ã‚¯ãƒªã‚¢ã—ã¾ã—ãŸ', 'success');
    showErrorLogs(); // å†è¡¨ç¤ºã—ã¦æ›´æ–°
  } catch (error) {
    console.error('ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã®ã‚¯ãƒªã‚¢ã«å¤±æ•—:', error);
    ErrorLogger.saveError(error, 'popup-clear-error-logs');
    showMessage('ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã®ã‚¯ãƒªã‚¢ã«å¤±æ•—ã—ã¾ã—ãŸ', 'error');
  }
}

