    // ã‚°ãƒ«ãƒ¼ãƒ—ã‚’ã‚¤ãƒ³ãƒãƒ¼ãƒˆ
    await chrome.storage.local.set({ calendarGroups: importData.groups });
    groups = importData.groups;

    // UIã‚’æ›´æ–°
    renderGroupList();
    showMessage(`${groupCount}å€‹ã®ã‚°ãƒ«ãƒ¼ãƒ—ã‚’ã‚¤ãƒ³ãƒãƒ¼ãƒˆã—ã¾ã—ãŸ`, 'success');

    // ãƒ•ã‚¡ã‚¤ãƒ«é¸æŠã‚’ãƒªã‚»ãƒƒãƒˆ
    event.target.value = '';
  } catch (error) {
    console.error('ã‚¤ãƒ³ãƒãƒ¼ãƒˆã‚¨ãƒ©ãƒ¼:', error);
    showMessage('ã‚¤ãƒ³ãƒãƒ¼ãƒˆã«å¤±æ•—ã—ã¾ã—ãŸ: ' + error.message, 'error');
    event.target.value = ''; // ãƒ•ã‚¡ã‚¤ãƒ«é¸æŠã‚’ãƒªã‚»ãƒƒãƒˆ
