// ãƒ‰ãƒ©ãƒƒã‚°ï¼†ãƒ‰ãƒ­ãƒƒãƒ—ã®è¨­å®š
let draggedElement = null;
let draggedIndex = null;

function setupDragAndDrop() {
  const items = document.querySelectorAll('.group-item');

  items.forEach(item => {
    item.addEventListener('dragstart', handleDragStart);
    item.addEventListener('dragenter', handleDragEnter);
    item.addEventListener('dragover', handleDragOver);
    item.addEventListener('dragleave', handleDragLeave);
    item.addEventListener('drop', handleDrop);
    item.addEventListener('dragend', handleDragEnd);
  });
}

function handleDragStart(e) {
  draggedElement = this;
  draggedIndex = parseInt(this.dataset.index);
  this.classList.add('dragging');
  e.dataTransfer.effectAllowed = 'move';
  e.dataTransfer.setData('text/html', this.innerHTML);
}

function handleDragEnter(e) {
  if (this !== draggedElement) {
    this.classList.add('drag-over');
  }
}

function handleDragOver(e) {
  if (e.preventDefault) {
    e.preventDefault();
  }
  e.dataTransfer.dropEffect = 'move';
  return false;
}

function handleDragLeave(e) {
  this.classList.remove('drag-over');
}

function handleDrop(e) {
  if (e.stopPropagation) {
    e.stopPropagation();
  }

  const dropIndex = parseInt(this.dataset.index);

  if (draggedElement !== this) {
    // é…åˆ—å†…ã§è¦ç´ ã‚’ç§»å‹•
    const [movedGroup] = groups.splice(draggedIndex, 1);
    groups.splice(dropIndex, 0, movedGroup);

    // ä¿å­˜ã—ã¦å†æç”»
    chrome.storage.local.set({ calendarGroups: groups }).then(() => {
      renderGroupList();
    });
  }

  return false;
}

function handleDragEnd(e) {
  const items = document.querySelectorAll('.group-item');
  items.forEach(item => {
    item.classList.remove('drag-over', 'dragging');
  });
}

// ã‚°ãƒ«ãƒ¼ãƒ—ã®å±•é–‹/æŠ˜ã‚ŠãŸãŸã¿
function toggleGroupExpansion(index) {
  if (expandedGroups.has(index)) {
    expandedGroups.delete(index);
  } else {
    expandedGroups.add(index);
  }
  renderGroupList();
}

