async function exportSettingsFromBackground() {
  try {
    // ã‚¹ãƒˆãƒ¬ãƒ¼ã‚¸ã‹ã‚‰ã‚°ãƒ«ãƒ¼ãƒ—è¨­å®šã‚’å–å¾—
    const result = await chrome.storage.local.get(['groups']);
    const groups = result.groups || [];

    if (groups.length === 0) {
      // é€šçŸ¥ã‚’è¡¨ç¤º
      chrome.action.setBadgeText({ text: '!' });
      chrome.action.setBadgeBackgroundColor({ color: '#FFA000' });
      setTimeout(() => {
        chrome.action.setBadgeText({ text: '' });
      }, 2000);
      return;
    }

    // ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆãƒ‡ãƒ¼ã‚¿ã‚’ä½œæˆ
    const exportData = {
      version: 1,
      exportDate: new Date().toISOString(),
      groups: groups
    };

    // JSONã«å¤‰æ›ã—ã¦ã‚¯ãƒªãƒƒãƒ—ãƒœãƒ¼ãƒ‰ã«ã‚³ãƒ”ãƒ¼
    const jsonString = JSON.stringify(exportData, null, 2);
    await copyToClipboard(jsonString);

  } catch (error) {
    console.error('Export settings error:', error);
    ErrorLogger.saveError(error, 'background-export-settings');
