// è¨­å®šã‚’ã‚¤ãƒ³ãƒãƒ¼ãƒˆï¼ˆãƒãƒƒã‚¯ã‚°ãƒ©ã‚¦ãƒ³ãƒ‰ã‹ã‚‰ï¼‰
async function importSettingsFromBackground() {
  try {
    // ã‚ªãƒ•ã‚¹ã‚¯ãƒªãƒ¼ãƒ³ãƒ‰ã‚­ãƒ¥ãƒ¡ãƒ³ãƒˆã‚’ä½œæˆã—ã¦ãƒ•ã‚¡ã‚¤ãƒ«é¸æŠãƒ€ã‚¤ã‚¢ãƒ­ã‚°ã‚’è¡¨ç¤º
    await chrome.offscreen.createDocument({
      url: chrome.runtime.getURL('offscreen.html'),
      reasons: ['USER_MEDIA'],
      justification: 'Import settings from file'
    });

    // ãƒ•ã‚¡ã‚¤ãƒ«é¸æŠç”¨ã®ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸ã‚’é€ä¿¡
    const response = await chrome.runtime.sendMessage({
      action: 'selectAndReadFile'
    });

    await chrome.offscreen.closeDocument();

    if (response && response.success && response.content) {
      try {
        const importData = JSON.parse(response.content);

        // ãƒ‡ãƒ¼ã‚¿ã®æ¤œè¨¼
        if (!importData.groups || !Array.isArray(importData.groups)) {
          throw new Error('Invalid import file format');
        }

        // ç¾åœ¨ã®ã‚°ãƒ«ãƒ¼ãƒ—ã‚’å–å¾—
        const currentResult = await chrome.storage.local.get(['groups']);
        const currentGroups = currentResult.groups || [];

        // æ–°ã—ã„ã‚°ãƒ«ãƒ¼ãƒ—ã‚’è¿½åŠ ï¼ˆæ—¢å­˜ã®ã‚‚ã®ã¯ä¸Šæ›¸ãã—ãªã„ï¼‰
        const groupNames = new Set(currentGroups.map(g => g.name));
        const newGroups = importData.groups.filter(g => !groupNames.has(g.name));

        if (newGroups.length > 0) {
          const updatedGroups = [...currentGroups, ...newGroups];
          await chrome.storage.local.set({ groups: updatedGroups });

          // æˆåŠŸã‚’é€šçŸ¥
          chrome.action.setBadgeText({ text: 'âœ“' });
          chrome.action.setBadgeBackgroundColor({ color: '#4CAF50' });
        } else {
          // æ–°ã—ã„ã‚°ãƒ«ãƒ¼ãƒ—ãŒãªã„å ´åˆ
          chrome.action.setBadgeText({ text: '=' });
          chrome.action.setBadgeBackgroundColor({ color: '#2196F3' });
        }

        setTimeout(() => {
          chrome.action.setBadgeText({ text: '' });
        }, 2000);

      } catch (parseError) {
        console.error('Import parse error:', parseError);
        chrome.action.setBadgeText({ text: 'âœ—' });
        chrome.action.setBadgeBackgroundColor({ color: '#F44336' });
        setTimeout(() => {
          chrome.action.setBadgeText({ text: '' });
        }, 2000);
      }
    }

  } catch (error) {
    console.error('Import settings error:', error);
    ErrorLogger.saveError(error, 'background-import-settings');
  }
}

