async function clearErrorLogsFromBackground() {
  try {
    await chrome.storage.local.set({ errorLogs: [] });
    // é€šçŸ¥ã‚’è¡¨ç¤º
    chrome.action.setBadgeText({ text: 'âœ“' });
    chrome.action.setBadgeBackgroundColor({ color: '#4CAF50' });

    // 2ç§’å¾Œã«ãƒãƒƒã‚¸ã‚’ã‚¯ãƒªã‚¢
    setTimeout(() => {
