async function copyErrorLogsFromBackground() {
  try {
    const result = await chrome.storage.local.get(['errorLogs']);
    const errorLogs = result.errorLogs || [];

    let logText = '=== ã‚¨ãƒ©ãƒ¼ãƒ­ã‚° ===\n\n';

    if (errorLogs.length === 0) {
      logText += 'ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã¯ã‚ã‚Šã¾ã›ã‚“';
    } else {
      logText += `åˆè¨ˆ ${errorLogs.length} ä»¶ã®ã‚¨ãƒ©ãƒ¼\n\n`;

      errorLogs.forEach((log, index) => {
        const date = new Date(log.timestamp);
        const timeStr = date.toLocaleString('ja-JP');

        logText += `--- ã‚¨ãƒ©ãƒ¼ ${index + 1} ---\n`;
        logText += `æ™‚åˆ»: ${timeStr}\n`;
        logText += `ã‚³ãƒ³ãƒ†ã‚­ã‚¹ãƒˆ: ${log.context}\n`;
        logText += `ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸: ${log.message}\n`;
        if (log.stack) {
          logText += `ã‚¹ã‚¿ãƒƒã‚¯: ${log.stack}\n`;
        }
        if (log.url) {
          logText += `URL: ${log.url}\n`;
        }
        logText += '\n';
      });
    }

    await copyToClipboard(logText);

  } catch (error) {
    console.error('Copy error logs error:', error);
    ErrorLogger.saveError(error, 'background-copy-error-logs');
    await copyToClipboard(`ã‚¨ãƒ©ãƒ¼: ${error.message}`);
  }
}

// ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã‚’ã‚¯ãƒªã‚¢ï¼ˆãƒãƒƒã‚¯ã‚°ãƒ©ã‚¦ãƒ³ãƒ‰ã‹ã‚‰ï¼‰
async function clearErrorLogsFromBackground() {
  try {
    await chrome.storage.local.set({ errorLogs: [] });
    // é€šçŸ¥ã‚’è¡¨ç¤º
    chrome.action.setBadgeText({ text: 'âœ“' });
    chrome.action.setBadgeBackgroundColor({ color: '#4CAF50' });

    // 2ç§’å¾Œã«ãƒãƒƒã‚¸ã‚’ã‚¯ãƒªã‚¢
    setTimeout(() => {
      chrome.action.setBadgeText({ text: '' });
