}

// ã‚¯ãƒªãƒƒãƒ—ãƒœãƒ¼ãƒ‰ã«ã‚³ãƒ”ãƒ¼ï¼ˆãƒãƒƒã‚¯ã‚°ãƒ©ã‚¦ãƒ³ãƒ‰ç”¨ï¼‰
async function copyToClipboard(text) {
  try {
    // ã‚ªãƒ•ã‚¹ã‚¯ãƒªãƒ¼ãƒ³ãƒ‰ã‚­ãƒ¥ãƒ¡ãƒ³ãƒˆã‚’ä½¿ç”¨ã—ã¦ã‚¯ãƒªãƒƒãƒ—ãƒœãƒ¼ãƒ‰ã«ã‚³ãƒ”ãƒ¼
    await chrome.offscreen.createDocument({
      url: chrome.runtime.getURL('offscreen.html'),
      reasons: ['CLIPBOARD'],
      justification: 'Copy debug info or error logs to clipboard'
    });

    await chrome.runtime.sendMessage({
      action: 'copyToClipboard',
      text: text
    });

    await chrome.offscreen.closeDocument();

    // æˆåŠŸã‚’ç¤ºã™ãƒãƒƒã‚¸ã‚’è¡¨ç¤º
    chrome.action.setBadgeText({ text: 'âœ“' });
    chrome.action.setBadgeBackgroundColor({ color: '#4CAF50' });

    // 2ç§’å¾Œã«ãƒãƒƒã‚¸ã‚’ã‚¯ãƒªã‚¢
    setTimeout(() => {
      chrome.action.setBadgeText({ text: '' });
    }, 2000);

  } catch (error) {
    console.error('Clipboard copy error:', error);
    // ã‚ªãƒ•ã‚¹ã‚¯ãƒªãƒ¼ãƒ³ãƒ‰ã‚­ãƒ¥ãƒ¡ãƒ³ãƒˆãŒæ—¢ã«å­˜åœ¨ã™ã‚‹å ´åˆã¯é–‰ã˜ã¦ãƒªãƒˆãƒ©ã‚¤
    try {
      await chrome.offscreen.closeDocument();
      await copyToClipboard(text);
    } catch (retryError) {
      ErrorLogger.saveError(retryError, 'background-clipboard');
    }
  }
}

// è¨­å®šã‚’ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆï¼ˆãƒãƒƒã‚¯ã‚°ãƒ©ã‚¦ãƒ³ãƒ‰ã‹ã‚‰ï¼‰
async function exportSettingsFromBackground() {
  try {
    // ã‚¹ãƒˆãƒ¬ãƒ¼ã‚¸ã‹ã‚‰ã‚°ãƒ«ãƒ¼ãƒ—è¨­å®šã‚’å–å¾—
    const result = await chrome.storage.local.get(['groups']);
    const groups = result.groups || [];

    if (groups.length === 0) {
      // é€šçŸ¥ã‚’è¡¨ç¤º
      chrome.action.setBadgeText({ text: '!' });
      chrome.action.setBadgeBackgroundColor({ color: '#FFA000' });
      setTimeout(() => {
        chrome.action.setBadgeText({ text: '' });
      }, 2000);
      return;
    }

    // ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆãƒ‡ãƒ¼ã‚¿ã‚’ä½œæˆ
    const exportData = {
      version: 1,
      exportDate: new Date().toISOString(),
      groups: groups
    };

    // JSONã«å¤‰æ›ã—ã¦ã‚¯ãƒªãƒƒãƒ—ãƒœãƒ¼ãƒ‰ã«ã‚³ãƒ”ãƒ¼
    const jsonString = JSON.stringify(exportData, null, 2);
    await copyToClipboard(jsonString);

  } catch (error) {
    console.error('Export settings error:', error);
    ErrorLogger.saveError(error, 'background-export-settings');
  }
}

// è¨­å®šã‚’ã‚¤ãƒ³ãƒãƒ¼ãƒˆï¼ˆãƒãƒƒã‚¯ã‚°ãƒ©ã‚¦ãƒ³ãƒ‰ã‹ã‚‰ï¼‰
async function importSettingsFromBackground() {
  try {
    // ã‚¤ãƒ³ãƒãƒ¼ãƒˆæ–¹æ³•ã®æ¡ˆå†…ã‚’ã‚¯ãƒªãƒƒãƒ—ãƒœãƒ¼ãƒ‰ã«ã‚³ãƒ”ãƒ¼
    const instructions = `=== è¨­å®šã®ã‚¤ãƒ³ãƒãƒ¼ãƒˆæ–¹æ³• ===

1. æ‹¡å¼µæ©Ÿèƒ½ã®ã‚¢ã‚¤ã‚³ãƒ³ã‚’ã‚¯ãƒªãƒƒã‚¯ã—ã¦ãƒãƒƒãƒ—ã‚¢ãƒƒãƒ—ã‚’é–‹ã
2. ã€Œè¨­å®šã®ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ãƒ»å¾©å…ƒã€ã‚»ã‚¯ã‚·ãƒ§ãƒ³ã®ã€Œè¨­å®šã‚’ã‚¤ãƒ³ãƒãƒ¼ãƒˆã€ãƒœã‚¿ãƒ³ã‚’ã‚¯ãƒªãƒƒã‚¯
3. ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆã—ãŸJSONãƒ•ã‚¡ã‚¤ãƒ«ã‚’é¸æŠ

â€» å³ã‚¯ãƒªãƒƒã‚¯ãƒ¡ãƒ‹ãƒ¥ãƒ¼ã‹ã‚‰ã®ãƒ•ã‚¡ã‚¤ãƒ«é¸æŠã¯ã‚»ã‚­ãƒ¥ãƒªãƒ†ã‚£ä¸Šã®åˆ¶é™ã«ã‚ˆã‚Šå®Ÿè¡Œã§ãã¾ã›ã‚“ã€‚
â€» ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆã—ãŸJSONãƒ‡ãƒ¼ã‚¿ã¯ã‚¯ãƒªãƒƒãƒ—ãƒœãƒ¼ãƒ‰ã«ã‚³ãƒ”ãƒ¼ã•ã‚Œã¦ã„ã¾ã™ã€‚`;

    await copyToClipboard(instructions);

    if (response && response.success && response.content) {
      try {
        const importData = JSON.parse(response.content);

        // ãƒ‡ãƒ¼ã‚¿ã®æ¤œè¨¼
        if (!importData.groups || !Array.isArray(importData.groups)) {
          throw new Error('Invalid import file format');
        }

        // ç¾åœ¨ã®ã‚°ãƒ«ãƒ¼ãƒ—ã‚’å–å¾—
        const currentResult = await chrome.storage.local.get(['groups']);
        const currentGroups = currentResult.groups || [];

        // æ–°ã—ã„ã‚°ãƒ«ãƒ¼ãƒ—ã‚’è¿½åŠ ï¼ˆæ—¢å­˜ã®ã‚‚ã®ã¯ä¸Šæ›¸ãã—ãªã„ï¼‰
        const groupNames = new Set(currentGroups.map(g => g.name));
        const newGroups = importData.groups.filter(g => !groupNames.has(g.name));

        if (newGroups.length > 0) {
          const updatedGroups = [...currentGroups, ...newGroups];
          await chrome.storage.local.set({ groups: updatedGroups });

          // æˆåŠŸã‚’é€šçŸ¥
          chrome.action.setBadgeText({ text: 'âœ“' });
          chrome.action.setBadgeBackgroundColor({ color: '#4CAF50' });
        } else {
          // æ–°ã—ã„ã‚°ãƒ«ãƒ¼ãƒ—ãŒãªã„å ´åˆ
          chrome.action.setBadgeText({ text: '=' });
          chrome.action.setBadgeBackgroundColor({ color: '#2196F3' });
        }

        setTimeout(() => {
          chrome.action.setBadgeText({ text: '' });
        }, 2000);

      } catch (parseError) {
        console.error('Import parse error:', parseError);
        chrome.action.setBadgeText({ text: 'âœ—' });
        chrome.action.setBadgeBackgroundColor({ color: '#F44336' });
        setTimeout(() => {
          chrome.action.setBadgeText({ text: '' });
        }, 2000);
      }
    }

  } catch (error) {
    console.error('Import settings error:', error);
    ErrorLogger.saveError(error, 'background-import-settings');
  }
}

