  const tabs = await chrome.tabs.query({active: true, currentWindow: true});
  if (tabs[0].url.includes('calendar.google.com')) {
    try {
      const response = await chrome.tabs.sendMessage(tabs[0].id, {
        action: 'applyGroup',
