// ã‚°ãƒ«ãƒ¼ãƒ—ã®å‰Šé™¤
async function deleteGroup(index) {
  const group = groups[index];
  if (confirm(`ã‚°ãƒ«ãƒ¼ãƒ— "${group.name}" ã‚’å‰Šé™¤ã—ã¾ã™ã‹ï¼Ÿ`)) {
    groups.splice(index, 1);
