// ã‚ªãƒ¼ãƒˆã‚³ãƒ³ãƒ—ãƒªãƒ¼ãƒˆæ©Ÿèƒ½
function handleMemberInput(e) {
  const input = e.target;
  const lines = input.value.split('\n');
  const currentLine = lines[lines.length - 1];

  if (currentLine.length < 1) {
    hideAutocomplete();
