      if (response && response.success) {
        showMessage('è‡ªåˆ†ã®ã‚«ãƒ¬ãƒ³ãƒ€ãƒ¼ã®ã¿ã‚’è¡¨ç¤ºã—ã¾ã—ãŸ', 'success');

        // ãƒãƒƒãƒ—ã‚¢ãƒƒãƒ—ã‚’è‡ªå‹•ã§é–‰ã˜ã‚‹
        setTimeout(() => {
