    }
  }
}

// ã‚°ãƒ«ãƒ¼ãƒ—ã®ç·¨é›†
function editGroup(index) {
  const group = groups[index];

  // ç·¨é›†ãƒ¢ãƒ¼ãƒ‰ã«åˆ‡ã‚Šæ›¿ãˆ
  editingGroupIndex = index;

