  document.getElementById('groupName').value = group.name;
  document.getElementById('memberInput').value = group.members.join('\n');

  // ãƒœã‚¿ãƒ³ã¨ã‚¿ã‚¤ãƒˆãƒ«ã‚’å¤‰æ›´
  document.getElementById('createGroup').textContent = 'ã‚°ãƒ«ãƒ¼ãƒ—æ›´æ–°';
  document.querySelector('.create-section h3').textContent = 'ã‚°ãƒ«ãƒ¼ãƒ—ç·¨é›†';
