  const lines = input.value.split('\n');
  const currentLine = lines[lines.length - 1];

  if (currentLine.length < 1) {
    hideAutocomplete();
    return;
  }

  const matches = allCalendars.filter(cal => 
    cal.toLowerCase().includes(currentLine.toLowerCase())
