  width: 100%;
}

.group-item:hover {
  background-color: #e8f0fe;
}

.group-item.dragging {
  opacity: 0.5;
  transform: scale(0.95);
}

.group-item.drag-over {
  background-color: #c2e0ff;
  transform: scale(1.02);
}

.group-content {
  display: flex;
