  border: none;
  background: none;
  color: #5f6368;
  border-radius: 50%;
  cursor: pointer;
  transition: background-color 0.2s;
}

.menu-close:hover {
  background-color: #f8f9fa;
}

.menu-section {
  padding: 16px;
  border-bottom: 1px solid #e0e0e0;
}

.menu-section:last-child {
  border-bottom: none;
}

.menu-section h4 {
  margin: 0 0 12px 0;
  font-size: 14px;
  font-weight: 600;
  color: #3c4043;
}

.menu-section .btn {
  width: 100%;
  margin-bottom: 8px;
}

.menu-section .btn:last-child {
  margin-bottom: 0;
}

.menu-link {
  display: flex;
  align-items: center;
  gap: 8px;
  width: 100%;
  padding: 10px 12px;
  text-decoration: none;
  color: #3c4043;
  background-color: #f8f9fa;
  border-radius: 4px;
  transition: background-color 0.2s;
}

.menu-link:hover {
  background-color: #e8f0fe;
  color: #1a73e8;
}

.menu-link svg {
  flex-shrink: 0;
}


::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: #f1f1f1;
}

::-webkit-scrollbar-thumb {
  background: #888;
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: #555;
}

