// ã‚ªãƒ¼ãƒˆã‚³ãƒ³ãƒ—ãƒªãƒ¼ãƒˆã®è¡¨ç¤º
function showAutocomplete(matches, currentInput) {
  const list = document.getElementById('autocompleteList');
