    let calendarsInfo = 'å–å¾—å¤±æ•—';
    try {
      const response = await chrome.tabs.sendMessage(tabs[0].id, {action: 'getCalendars'});
      if (response && response.calendars) {
        calendarsInfo = `å–å¾—æˆåŠŸ: ${response.calendars.length}å€‹\n` + 
