// ã‚°ãƒ«ãƒ¼ãƒ—ãƒ‡ãƒ¼ã‚¿ã®ç®¡ç†
let groups = [];
let allCalendars = [];
let lastDebugInfo = '';
let editingGroupIndex = null;
let expandedGroups = new Set();

// ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ç®¡ç†
const ErrorLogger = {
  // ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã®æœ€å¤§ä¿å­˜æ•°
  MAX_ERROR_LOGS: 100,

  // ã‚¨ãƒ©ãƒ¼ã‚’ã‚¹ãƒˆãƒ¬ãƒ¼ã‚¸ã«ä¿å­˜
  async saveError(error, context = 'popup') {
    try {
      const errorLog = {
        timestamp: new Date().toISOString(),
        context: context,
        message: error.message || String(error),
        stack: error.stack || '',
        url: window.location.href,
        userAgent: navigator.userAgent
      };

      // æ—¢å­˜ã®ãƒ­ã‚°ã‚’å–å¾—
      const result = await chrome.storage.local.get(['errorLogs']);
      const errorLogs = result.errorLogs || [];

      // æ–°ã—ã„ã‚¨ãƒ©ãƒ¼ã‚’è¿½åŠ 
      errorLogs.unshift(errorLog);

      // æœ€å¤§ä¿å­˜æ•°ã‚’è¶…ãˆãŸã‚‰å¤ã„ã‚‚ã®ã‚’å‰Šé™¤
      if (errorLogs.length > this.MAX_ERROR_LOGS) {
        errorLogs.splice(this.MAX_ERROR_LOGS);
      }

      // ä¿å­˜
      await chrome.storage.local.set({ errorLogs });
      console.error('Error logged:', errorLog);
    } catch (saveError) {
      console.error('Failed to save error log:', saveError);
    }
  }
};

