function adjustBodyHeight() {
  const container = document.querySelector('.container');
  if (container) {
    // ä¸€æ™‚çš„ã«bodyã®heightã‚’autoã«ã—ã¦å®Ÿéš›ã®é«˜ã•ã‚’æ¸¬å®š
    const originalHeight = document.body.style.height;
    document.body.style.height = 'auto';

    // containerã®å®Ÿéš›ã®é«˜ã•ã‚’å–å¾—ï¼ˆpaddingã‚‚å«ã‚€ï¼‰
    const contentHeight = container.scrollHeight;

    // ã‚¹ã‚¯ãƒ­ãƒ¼ãƒ«ãƒãƒ¼ã®å¹…ã‚’è€ƒæ…®ï¼ˆé€šå¸¸17pxç¨‹åº¦ï¼‰
    const scrollbarWidth = window.innerWidth - document.documentElement.clientWidth;
    const extraHeight = scrollbarWidth > 0 ? 20 : 0;

    const maxHeight = window.innerHeight - 50; // ã‚¦ã‚£ãƒ³ãƒ‰ã‚¦ã‚ˆã‚Šå°‘ã—å°ã•ã
    const newHeight = Math.min(contentHeight + extraHeight, maxHeight);

    // é«˜ã•ã‚’è¨­å®š
