// DOMã®å¤‰æ›´ã‚’ç›£è¦–ã—ã¦bodyã®é«˜ã•ã‚’èª¿æ•´
const observer = new MutationObserver(() => {
  // ã‚°ãƒ«ãƒ¼ãƒ—ãŒèª­ã¿è¾¼ã¾ã‚ŒãŸå¾Œã®ã¿é«˜ã•ã‚’èª¿æ•´
  if (groupsLoaded) {
    adjustBodyHeight();
  }
});

// bodyè¦ç´ ã®ç›£è¦–ã‚’é–‹å§‹
observer.observe(document.body, {
  childList: true,
  subtree: true,
  attributes: true,
  attributeFilter: ['style', 'class']
});

// ã‚¦ã‚£ãƒ³ãƒ‰ã‚¦ãƒªã‚µã‚¤ã‚ºæ™‚ã«é«˜ã•ã‚’èª¿æ•´ï¼ˆã‚°ãƒ«ãƒ¼ãƒ—èª­ã¿è¾¼ã¿å¾Œã®ã¿ï¼‰
window.addEventListener('resize', () => {
