// ã‚³ãƒ³ãƒ†ãƒ³ãƒ„ã®é«˜ã•ã«å¿œã˜ã¦bodyã®é«˜ã•ã‚’èª¿æ•´
function adjustBodyHeight() {
  const container = document.querySelector('.container');
  if (container) {
    // ä¸€æ™‚çš„ã«bodyã®heightã‚’autoã«ã—ã¦å®Ÿéš›ã®é«˜ã•ã‚’æ¸¬å®š
    const originalHeight = document.body.style.height;
    document.body.style.height = 'auto';

    // containerã®å®Ÿéš›ã®é«˜ã•ã‚’å–å¾—ï¼ˆpaddingã‚‚å«ã‚€ï¼‰
    const contentHeight = container.scrollHeight;

    // ã‚¹ã‚¯ãƒ­ãƒ¼ãƒ«ãƒãƒ¼ã®å¹…ã‚’è€ƒæ…®ï¼ˆé€šå¸¸17pxç¨‹åº¦ï¼‰
    const scrollbarWidth = window.innerWidth - document.documentElement.clientWidth;
    const extraHeight = scrollbarWidth > 0 ? 20 : 0;

    const maxHeight = window.innerHeight - 50; // ã‚¦ã‚£ãƒ³ãƒ‰ã‚¦ã‚ˆã‚Šå°‘ã—å°ã•ã
    const newHeight = Math.min(contentHeight + extraHeight, maxHeight);

    // é«˜ã•ã‚’è¨­å®š
    document.body.style.height = newHeight + 'px';

    // ã¾ã ã‚¹ã‚¯ãƒ­ãƒ¼ãƒ«ãƒãƒ¼ãŒå‡ºã‚‹å ´åˆã¯å°‘ã—å¢—ã‚„ã™
    setTimeout(() => {
      if (document.body.scrollHeight > document.body.clientHeight) {
        const additionalHeight = Math.min(document.body.scrollHeight - document.body.clientHeight + 5, maxHeight - newHeight);
        document.body.style.height = (newHeight + additionalHeight) + 'px';
      }
    }, 10);
  }
}

// ã‚°ãƒ«ãƒ¼ãƒ—èª­ã¿è¾¼ã¿ãŒå®Œäº†ã—ãŸã‹ã©ã†ã‹ã®ãƒ•ãƒ©ã‚°
let groupsLoaded = false;

// DOMã®å¤‰æ›´ã‚’ç›£è¦–ã—ã¦bodyã®é«˜ã•ã‚’èª¿æ•´
const observer = new MutationObserver(() => {
  // ã‚°ãƒ«ãƒ¼ãƒ—ãŒèª­ã¿è¾¼ã¾ã‚ŒãŸå¾Œã®ã¿é«˜ã•ã‚’èª¿æ•´
  if (groupsLoaded) {
    adjustBodyHeight();
  }
});

// bodyè¦ç´ ã®ç›£è¦–ã‚’é–‹å§‹
observer.observe(document.body, {
  childList: true,
  subtree: true,
  attributes: true,
  attributeFilter: ['style', 'class']
});

// ã‚¦ã‚£ãƒ³ãƒ‰ã‚¦ãƒªã‚µã‚¤ã‚ºæ™‚ã«é«˜ã•ã‚’èª¿æ•´ï¼ˆã‚°ãƒ«ãƒ¼ãƒ—èª­ã¿è¾¼ã¿å¾Œã®ã¿ï¼‰
window.addEventListener('resize', () => {
  if (groupsLoaded) {
    adjustBodyHeight();
  }
});

