// ã‚°ãƒ«ãƒ¼ãƒ—ãƒ‡ãƒ¼ã‚¿ã®ç®¡ç†
let groups = [];
let allCalendars = [];
let lastDebugInfo = '';
let editingGroupIndex = null;
let expandedGroups = new Set();

// ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ç®¡ç†
const ErrorLogger = {
  // ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã®æœ€å¤§ä¿å­˜æ•°
  MAX_ERROR_LOGS: 100,

  // ã‚¨ãƒ©ãƒ¼ã‚’ã‚¹ãƒˆãƒ¬ãƒ¼ã‚¸ã«ä¿å­˜
  async saveError(error, context = 'popup') {
    try {
      const errorLog = {
        timestamp: new Date().toISOString(),
        context: context,
        message: error.message || String(error),
        stack: error.stack || '',
        url: window.location.href,
        userAgent: navigator.userAgent
      };

      // æ—¢å­˜ã®ãƒ­ã‚°ã‚’å–å¾—
      const result = await chrome.storage.local.get(['errorLogs']);
      const errorLogs = result.errorLogs || [];

      // æ–°ã—ã„ã‚¨ãƒ©ãƒ¼ã‚’è¿½åŠ 
      errorLogs.unshift(errorLog);

      // æœ€å¤§ä¿å­˜æ•°ã‚’è¶…ãˆãŸã‚‰å¤ã„ã‚‚ã®ã‚’å‰Šé™¤
      if (errorLogs.length > this.MAX_ERROR_LOGS) {
        errorLogs.splice(this.MAX_ERROR_LOGS);
      }

      // ä¿å­˜
      await chrome.storage.local.set({ errorLogs });
      console.error('Error logged:', errorLog);
    } catch (saveError) {
      console.error('Failed to save error log:', saveError);
    }
  }
};

// ã‚°ãƒ­ãƒ¼ãƒãƒ«ã‚¨ãƒ©ãƒ¼ãƒãƒ³ãƒ‰ãƒ©ãƒ¼
window.addEventListener('error', (event) => {
  ErrorLogger.saveError({
    message: event.message,
    stack: `${event.filename}:${event.lineno}:${event.colno}`,
    error: event.error
  });
});

// Promise rejection ãƒãƒ³ãƒ‰ãƒ©ãƒ¼
window.addEventListener('unhandledrejection', (event) => {
  ErrorLogger.saveError({
    message: `Unhandled Promise Rejection: ${event.reason}`,
    stack: event.reason?.stack || ''
  });
});

// åˆæœŸåŒ–
document.addEventListener('DOMContentLoaded', async () => {
  try {
    await loadGroups();
    await loadCalendars();
    renderGroupList();
    setupEventListeners();

    // ã‚°ãƒ«ãƒ¼ãƒ—èª­ã¿è¾¼ã¿å®Œäº†å¾Œã«ãƒ•ãƒ©ã‚°ã‚’è¨­å®šã—ã¦é«˜ã•ã‚’èª¿æ•´
    groupsLoaded = true;
    adjustBodyHeight();
  } catch (error) {
    console.error('åˆæœŸåŒ–ã‚¨ãƒ©ãƒ¼:', error);
    ErrorLogger.saveError(error, 'popup-init');
    showMessage('åˆæœŸåŒ–ã«å¤±æ•—ã—ã¾ã—ãŸã€‚ãƒšãƒ¼ã‚¸ã‚’ãƒªãƒ­ãƒ¼ãƒ‰ã—ã¦ãã ã•ã„ã€‚', 'error');
  }
});

// ã‚¤ãƒ™ãƒ³ãƒˆãƒªã‚¹ãƒŠãƒ¼ã®è¨­å®š
function setupEventListeners() {
  document.getElementById('createGroup').addEventListener('click', createGroup);
  document.getElementById('cancelEdit').addEventListener('click', cancelEdit);
  document.getElementById('memberInput').addEventListener('input', handleMemberInput);
  document.getElementById('memberInput').addEventListener('keydown', handleKeyDown);
  document.getElementById('copyDebugInfo').addEventListener('click', copyDebugInfo);
  document.getElementById('copyErrorLogs').addEventListener('click', copyErrorLogs);
  document.getElementById('showMyCalendarOnly').addEventListener('click', showMyCalendarOnly);

  // ãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ãƒ»å¾©å…ƒæ©Ÿèƒ½
  document.getElementById('exportSettings').addEventListener('click', exportSettings);
  document.getElementById('importSettingsBtn').addEventListener('click', () => {
    document.getElementById('importSettings').click();
  });
  document.getElementById('importSettings').addEventListener('change', importSettings);

  // ã‚¯ãƒªãƒƒã‚¯ã§ã‚ªãƒ¼ãƒˆã‚³ãƒ³ãƒ—ãƒªãƒ¼ãƒˆã‚’é–‰ã˜ã‚‹
  document.addEventListener('click', (e) => {
    if (!e.target.closest('.member-input-container')) {
      hideAutocomplete();
    }
  });
}

// ä¿å­˜æ¸ˆã¿ã‚°ãƒ«ãƒ¼ãƒ—ã®èª­ã¿è¾¼ã¿
async function loadGroups() {
  const result = await chrome.storage.local.get(['calendarGroups']);
  groups = result.calendarGroups || [];
}

// ã‚«ãƒ¬ãƒ³ãƒ€ãƒ¼ãƒªã‚¹ãƒˆã®èª­ã¿è¾¼ã¿
async function loadCalendars() {
  try {
    // content.jsã‹ã‚‰ã‚«ãƒ¬ãƒ³ãƒ€ãƒ¼ãƒªã‚¹ãƒˆã‚’å–å¾—
    const tabs = await chrome.tabs.query({active: true, currentWindow: true});
    if (!tabs || tabs.length === 0) {
      console.error('ã‚¢ã‚¯ãƒ†ã‚£ãƒ–ãªã‚¿ãƒ–ãŒè¦‹ã¤ã‹ã‚Šã¾ã›ã‚“');
      return;
    }

    if (tabs[0].url && tabs[0].url.includes('calendar.google.com')) {
      try {
        // ã‚¿ãƒ–ãŒã¾ã èª­ã¿è¾¼ã¿ä¸­ã®å ´åˆã¯å°‘ã—å¾…ã¤
        if (tabs[0].status !== 'complete') {
          await new Promise(resolve => setTimeout(resolve, 100));
        }

        const response = await chrome.tabs.sendMessage(tabs[0].id, {action: 'getCalendars'});
        if (response && response.calendars) {
          allCalendars = response.calendars;
        }
      } catch (error) {
        console.error('ã‚«ãƒ¬ãƒ³ãƒ€ãƒ¼ãƒªã‚¹ãƒˆã®å–å¾—ã«å¤±æ•—:', error);
        // ã‚¨ãƒ©ãƒ¼ã‚’ç„¡è¦–ã—ã¦ç¶šè¡Œï¼ˆã‚ªãƒ¼ãƒˆã‚³ãƒ³ãƒ—ãƒªãƒ¼ãƒˆãŒä½¿ãˆãªã„ã ã‘ï¼‰
      }
    }
  } catch (error) {
    console.error('ã‚¿ãƒ–ã‚¯ã‚¨ãƒªã‚¨ãƒ©ãƒ¼:', error);
  }
}

// ã‚°ãƒ«ãƒ¼ãƒ—ãƒªã‚¹ãƒˆã®è¡¨ç¤º
function renderGroupList() {
  const groupListElement = document.getElementById('groupList');

  if (groups.length === 0) {
    groupListElement.innerHTML = '<p style="color: #5f6368; text-align: center;">ä¿å­˜æ¸ˆã¿ã‚°ãƒ«ãƒ¼ãƒ—ã¯ã‚ã‚Šã¾ã›ã‚“</p>';
    return;
  }

  groupListElement.innerHTML = groups.map((group, index) => {
    const isExpanded = expandedGroups.has(index);
    return `
    <div class="group-item ${isExpanded ? 'expanded' : ''}" data-index="${index}" draggable="true">
      <div class="group-content">
        <div class="drag-handle" title="ãƒ‰ãƒ©ãƒƒã‚°ã—ã¦ä¸¦ã³æ›¿ãˆ">
          <svg width="16" height="16" viewBox="0 0 24 24" fill="currentColor">
            <circle cx="9" cy="5" r="1.5"/>
            <circle cx="15" cy="5" r="1.5"/>
            <circle cx="9" cy="12" r="1.5"/>
            <circle cx="15" cy="12" r="1.5"/>
            <circle cx="9" cy="19" r="1.5"/>
            <circle cx="15" cy="19" r="1.5"/>
          </svg>
        </div>
        <div class="group-info" data-index="${index}">
          <div class="group-header">
            <div class="expand-icon">
              <svg width="12" height="12" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                <polyline points="${isExpanded ? '6 9 12 15 18 9' : '9 6 15 12 9 18'}"></polyline>
              </svg>
            </div>
            <div class="group-name">${escapeHtml(group.name)}</div>
            <div class="group-count">(${group.members.length}äºº)</div>
          </div>
        </div>
        <div class="group-actions">
          <button class="btn btn-primary btn-small apply-btn" data-index="${index}">é©ç”¨</button>
          <button class="btn btn-secondary btn-small edit-btn" data-index="${index}">ç·¨é›†</button>
          <button class="btn btn-danger btn-small delete-btn" data-index="${index}">å‰Šé™¤</button>
        </div>
      </div>
      ${isExpanded ? `
        <div class="group-members-list">
          ${group.members.map(member => `
            <div class="member-item">
              <svg width="12" height="12" viewBox="0 0 24 24" fill="none" stroke="currentColor" stroke-width="2">
                <path d="M20 21v-2a4 4 0 0 0-4-4H8a4 4 0 0 0-4 4v2"></path>
                <circle cx="12" cy="7" r="4"></circle>
              </svg>
              ${escapeHtml(member)}
            </div>
          `).join('')}
        </div>
      ` : ''}
    </div>
  `}).join('');

  // ã‚¤ãƒ™ãƒ³ãƒˆãƒªã‚¹ãƒŠãƒ¼ã‚’è¨­å®š
  document.querySelectorAll('.group-info').forEach(info => {
    info.addEventListener('click', (e) => {
      // ãƒœã‚¿ãƒ³ã®ã‚¯ãƒªãƒƒã‚¯ã¯é™¤å¤–
      if (!e.target.closest('.group-actions')) {
        const index = parseInt(info.dataset.index);
        toggleGroupExpansion(index);
      }
    });
  });

  document.querySelectorAll('.apply-btn').forEach(btn => {
    btn.addEventListener('click', (e) => {
      const index = parseInt(e.target.dataset.index);
      applyGroup(index);
    });
  });

  document.querySelectorAll('.edit-btn').forEach(btn => {
    btn.addEventListener('click', (e) => {
      const index = parseInt(e.target.dataset.index);
      editGroup(index);
    });
  });

  document.querySelectorAll('.delete-btn').forEach(btn => {
    btn.addEventListener('click', (e) => {
      const index = parseInt(e.target.dataset.index);
      deleteGroup(index);
    });
  });

  // ãƒ‰ãƒ©ãƒƒã‚°ï¼†ãƒ‰ãƒ­ãƒƒãƒ—ã®ã‚¤ãƒ™ãƒ³ãƒˆãƒªã‚¹ãƒŠãƒ¼
  setupDragAndDrop();
}

// ç·¨é›†ã‚’ã‚­ãƒ£ãƒ³ã‚»ãƒ«
function cancelEdit() {
