    // ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯: ãƒ†ã‚­ã‚¹ãƒˆã‚¨ãƒªã‚¢ã‚’ä½¿ç”¨
    try {
      const result = await chrome.storage.local.get(['errorLogs']);
      const errorLogs = result.errorLogs || [];
      let logText = '=== ã‚¨ãƒ©ãƒ¼ãƒ­ã‚° ===\n\n';

      if (errorLogs.length === 0) {
        logText += 'ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã¯ã‚ã‚Šã¾ã›ã‚“';
        showMessage('ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã¯ã‚ã‚Šã¾ã›ã‚“', 'info');
        return;
      } else {
        logText += `åˆè¨ˆ ${errorLogs.length} ä»¶ã®ã‚¨ãƒ©ãƒ¼\n\n`;
        errorLogs.forEach((log, index) => {
          const date = new Date(log.timestamp);
          const timeStr = date.toLocaleString('ja-JP');
          logText += `--- ã‚¨ãƒ©ãƒ¼ ${index + 1} ---\n`;
          logText += `æ™‚åˆ»: ${timeStr}\n`;
          logText += `ã‚³ãƒ³ãƒ†ã‚­ã‚¹ãƒˆ: ${log.context}\n`;
          logText += `ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸: ${log.message}\n`;
          if (log.stack) logText += `ã‚¹ã‚¿ãƒƒã‚¯: ${log.stack}\n`;
          if (log.url) logText += `URL: ${log.url}\n`;
          logText += '\n';
        });
      }

      const textarea = document.createElement('textarea');
      textarea.value = logText;
      textarea.style.position = 'fixed';
      textarea.style.opacity = '0';
      document.body.appendChild(textarea);
      textarea.select();

      document.execCommand('copy');
      document.body.removeChild(textarea);
      showMessage('ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã‚’ã‚³ãƒ”ãƒ¼ã—ã¾ã—ãŸ', 'success');
    } catch (e) {
      showMessage('ã‚³ãƒ”ãƒ¼ã«å¤±æ•—ã—ã¾ã—ãŸ', 'error');
