body {
  width: 400px;
  height: 800px;
  margin: 0;
  padding: 0;
  font-family: 'Roboto', Arial, sans-serif;
  font-size: 14px;
  background-color: #f5f5f5;
  overflow-y: auto;
}

.container {
  padding: 16px;
  overflow-x: hidden;
}

.header {
