  justify-content: center;
  width: 32px;
  height: 32px;
  padding: 6px;
  border: none;
  background: none;
  color: #5f6368;
  border-radius: 50%;
  cursor: pointer;
  transition: background-color 0.2s, color 0.2s;
}

.menu-toggle:hover {
  background-color: #f8f9fa;
  color: #3c4043;
}

.menu-toggle svg {
  display: block;
}

h3 {
  margin: 12px 0 8px 0;
  font-size: 14px;
  color: #3c4043;
  font-weight: 500;
}

.quick-actions {
  margin-bottom: 12px;
}

.quick-action-btn {
  width: 100%;
