  transition: background-color 0.2s;
}

.autocomplete-item:hover {
  background-color: #f8f9fa;
}

.autocomplete-item.selected {
  background-color: #e8f0fe;
}

.message {
  padding: 8px 12px;
  border-radius: 4px;
  margin-top: 12px;
  text-align: center;
  font-size: 13px;
  display: none;
}

