}

.menu-close:hover {
  background-color: #f8f9fa;
}

.menu-section {
  padding: 16px;
  border-bottom: 1px solid #e0e0e0;
}

.menu-section:last-child {
  border-bottom: none;
}

.menu-section h4 {
  margin: 0 0 12px 0;
  font-size: 14px;
  font-weight: 600;
  color: #3c4043;
}

.menu-section .btn {
  width: 100%;
  margin-bottom: 8px;
}

.menu-section .btn:last-child {
  margin-bottom: 0;
}

.menu-link {
  display: flex;
  align-items: center;
  gap: 8px;
  width: 100%;
  padding: 10px 12px;
