// ã‚°ãƒ«ãƒ¼ãƒ—ã®ä½œæˆ
async function createGroup() {
  const nameInput = document.getElementById('groupName');
  const memberInput = document.getElementById('memberInput');

  const name = nameInput.value.trim();
  const members = memberInput.value.split('\n').map(m => m.trim()).filter(m => m);

  if (!name || members.length === 0) {
    showMessage('ã‚°ãƒ«ãƒ¼ãƒ—åã¨ãƒ¡ãƒ³ãƒãƒ¼ã‚’å…¥åŠ›ã—ã¦ãã ã•ã„', 'error');
    return;
  }

  if (editingGroupIndex !== null) {
    // ç·¨é›†ãƒ¢ãƒ¼ãƒ‰
    groups[editingGroupIndex] = { name, members };
    showMessage('ã‚°ãƒ«ãƒ¼ãƒ—ã‚’æ›´æ–°ã—ã¾ã—ãŸ', 'success');
    cancelEdit();
  } else {
    // æ–°è¦ä½œæˆãƒ¢ãƒ¼ãƒ‰
    groups.push({ name, members });
    showMessage('ã‚°ãƒ«ãƒ¼ãƒ—ã‚’ä½œæˆã—ã¾ã—ãŸ', 'success');
    nameInput.value = '';
    memberInput.value = '';
  }

  await chrome.storage.local.set({ calendarGroups: groups });
  renderGroupList();
}

