    const result = await chrome.storage.local.get(['calendarGroups']);
    const groups = result.calendarGroups || [];

    const exportData = {
      version: '1.0',
      exportDate: new Date().toISOString(),
      groups: groups
    };

    const dataStr = JSON.stringify(exportData, null, 2);
    const dataBlob = new Blob([dataStr], { type: 'application/json' });

    const a = document.createElement('a');
    a.href = URL.createObjectURL(dataBlob);
    a.download = `gcal-groups-backup-${new Date().toISOString().split('T')[0]}.json`;
    document.body.appendChild(a);
    a.click();
    document.body.removeChild(a);

    showMessage('è¨­å®šã‚’ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆã—ã¾ã—ãŸ', 'success');
  } catch (error) {
    console.error('ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆã‚¨ãƒ©ãƒ¼:', error);
    showMessage('ã‚¨ã‚¯ã‚¹ãƒãƒ¼ãƒˆã«å¤±æ•—ã—ã¾ã—ãŸ', 'error');
  }
}

// è¨­å®šã®ã‚¤ãƒ³ãƒãƒ¼ãƒˆ
async function importSettings(event) {
  const file = event.target.files[0];
  if (!file) return;

  try {
    const text = await file.text();
    const importData = JSON.parse(text);

    // ãƒ‡ãƒ¼ã‚¿ã®æ¤œè¨¼
    if (!importData.version || !importData.groups || !Array.isArray(importData.groups)) {
      throw new Error('ç„¡åŠ¹ãªãƒãƒƒã‚¯ã‚¢ãƒƒãƒ—ãƒ•ã‚¡ã‚¤ãƒ«ã§ã™');
    }

    // ç¢ºèªãƒ€ã‚¤ã‚¢ãƒ­ã‚°
    const groupCount = importData.groups.length;
    const confirmMessage = `${groupCount}å€‹ã®ã‚°ãƒ«ãƒ¼ãƒ—ã‚’ã‚¤ãƒ³ãƒãƒ¼ãƒˆã—ã¾ã™ã€‚\nç¾åœ¨ã®è¨­å®šã¯ä¸Šæ›¸ãã•ã‚Œã¾ã™ã€‚ç¶šè¡Œã—ã¾ã™ã‹ï¼Ÿ`;

    if (!confirm(confirmMessage)) {
      event.target.value = ''; // ãƒ•ã‚¡ã‚¤ãƒ«é¸æŠã‚’ãƒªã‚»ãƒƒãƒˆ
      return;
    }

    // ã‚°ãƒ«ãƒ¼ãƒ—ã‚’ã‚¤ãƒ³ãƒãƒ¼ãƒˆ
    await chrome.storage.local.set({ calendarGroups: importData.groups });
    groups = importData.groups;

    // UIã‚’æ›´æ–°
    renderGroupList();
    showMessage(`${groupCount}å€‹ã®ã‚°ãƒ«ãƒ¼ãƒ—ã‚’ã‚¤ãƒ³ãƒãƒ¼ãƒˆã—ã¾ã—ãŸ`, 'success');

    // ãƒ•ã‚¡ã‚¤ãƒ«é¸æŠã‚’ãƒªã‚»ãƒƒãƒˆ
    event.target.value = '';
  } catch (error) {
    console.error('ã‚¤ãƒ³ãƒãƒ¼ãƒˆã‚¨ãƒ©ãƒ¼:', error);
    showMessage('ã‚¤ãƒ³ãƒãƒ¼ãƒˆã«å¤±æ•—ã—ã¾ã—ãŸ: ' + error.message, 'error');
    event.target.value = ''; // ãƒ•ã‚¡ã‚¤ãƒ«é¸æŠã‚’ãƒªã‚»ãƒƒãƒˆ
  }
}

// ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã‚’ã‚¯ãƒªãƒƒãƒ—ãƒœãƒ¼ãƒ‰ã«ã‚³ãƒ”ãƒ¼
async function copyErrorLogs() {
  console.log('copyErrorLogs called');
  try {
    showMessage('ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã‚’å–å¾—ä¸­...', 'info');

    const result = await chrome.storage.local.get(['errorLogs']);
    console.log('Storage result:', result);
    const errorLogs = result.errorLogs || [];
    console.log('Error logs count:', errorLogs.length);

    let logText = '=== ã‚¨ãƒ©ãƒ¼ãƒ­ã‚° ===\n\n';

    if (errorLogs.length === 0) {
      logText += 'ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã¯ã‚ã‚Šã¾ã›ã‚“';
      console.log('No error logs found, showing message');
      showMessage('ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã¯ã‚ã‚Šã¾ã›ã‚“', 'info');
      return;
    } else {
      logText += `åˆè¨ˆ ${errorLogs.length} ä»¶ã®ã‚¨ãƒ©ãƒ¼\n\n`;

      errorLogs.forEach((log, index) => {
        const date = new Date(log.timestamp);
        const timeStr = date.toLocaleString('ja-JP');

        logText += `--- ã‚¨ãƒ©ãƒ¼ ${index + 1} ---\n`;
        logText += `æ™‚åˆ»: ${timeStr}\n`;
        logText += `ã‚³ãƒ³ãƒ†ã‚­ã‚¹ãƒˆ: ${log.context}\n`;
        logText += `ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸: ${log.message}\n`;
        if (log.stack) {
          logText += `ã‚¹ã‚¿ãƒƒã‚¯: ${log.stack}\n`;
        }
        if (log.url) {
          logText += `URL: ${log.url}\n`;
        }
        logText += '\n';
      });
    }

    await navigator.clipboard.writeText(logText);
    showMessage('ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã‚’ã‚³ãƒ”ãƒ¼ã—ã¾ã—ãŸ', 'success');
  } catch (error) {
    console.error('Clipboard API failed:', error);
    // ãƒ•ã‚©ãƒ¼ãƒ«ãƒãƒƒã‚¯: ãƒ†ã‚­ã‚¹ãƒˆã‚¨ãƒªã‚¢ã‚’ä½¿ç”¨
    try {
      const result = await chrome.storage.local.get(['errorLogs']);
      const errorLogs = result.errorLogs || [];
      let logText = '=== ã‚¨ãƒ©ãƒ¼ãƒ­ã‚° ===\n\n';

      if (errorLogs.length === 0) {
        logText += 'ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã¯ã‚ã‚Šã¾ã›ã‚“';
        showMessage('ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã¯ã‚ã‚Šã¾ã›ã‚“', 'info');
        return;
      } else {
        logText += `åˆè¨ˆ ${errorLogs.length} ä»¶ã®ã‚¨ãƒ©ãƒ¼\n\n`;
        errorLogs.forEach((log, index) => {
          const date = new Date(log.timestamp);
          const timeStr = date.toLocaleString('ja-JP');
          logText += `--- ã‚¨ãƒ©ãƒ¼ ${index + 1} ---\n`;
          logText += `æ™‚åˆ»: ${timeStr}\n`;
          logText += `ã‚³ãƒ³ãƒ†ã‚­ã‚¹ãƒˆ: ${log.context}\n`;
          logText += `ãƒ¡ãƒƒã‚»ãƒ¼ã‚¸: ${log.message}\n`;
          if (log.stack) logText += `ã‚¹ã‚¿ãƒƒã‚¯: ${log.stack}\n`;
          if (log.url) logText += `URL: ${log.url}\n`;
          logText += '\n';
        });
      }

      const textarea = document.createElement('textarea');
      textarea.value = logText;
      textarea.style.position = 'fixed';
      textarea.style.opacity = '0';
      document.body.appendChild(textarea);
      textarea.select();

      document.execCommand('copy');
      document.body.removeChild(textarea);
      showMessage('ã‚¨ãƒ©ãƒ¼ãƒ­ã‚°ã‚’ã‚³ãƒ”ãƒ¼ã—ã¾ã—ãŸ', 'success');
    } catch (e) {
      showMessage('ã‚³ãƒ”ãƒ¼ã«å¤±æ•—ã—ã¾ã—ãŸ', 'error');
      ErrorLogger.saveError(e, 'popup-copy-error-logs');
    }
  }
}

