// ã‚ªãƒ¼ãƒˆã‚³ãƒ³ãƒ—ãƒªãƒ¼ãƒˆã®éè¡¨ç¤º
function hideAutocomplete() {
  document.getElementById('autocompleteList').style.display = 'none';
}

// ã‚ªãƒ¼ãƒˆã‚³ãƒ³ãƒ—ãƒªãƒ¼ãƒˆé …ç›®ã®é¸æŠ
function selectAutocompleteItem(value) {
  const input = document.getElementById('memberInput');
  const lines = input.value.split('\n');
  lines[lines.length - 1] = value;
  input.value = lines.join('\n') + '\n';
  input.focus();
  hideAutocomplete();
}

// ã‚­ãƒ¼ãƒœãƒ¼ãƒ‰æ“ä½œ
function handleKeyDown(e) {
  const list = document.getElementById('autocompleteList');
  const items = list.querySelectorAll('.autocomplete-item');
  const selected = list.querySelector('.autocomplete-item.selected');

  if (e.key === 'ArrowDown' || e.key === 'ArrowUp') {
    e.preventDefault();

    if (items.length === 0) return;

    let index = Array.from(items).indexOf(selected);

    if (e.key === 'ArrowDown') {
      index = index < items.length - 1 ? index + 1 : 0;
    } else {
      index = index > 0 ? index - 1 : items.length - 1;
    }

    items.forEach(item => item.classList.remove('selected'));
    items[index].classList.add('selected');
    items[index].scrollIntoView({ block: 'nearest' });
  } else if (e.key === 'Enter' && selected) {
    e.preventDefault();
    selectAutocompleteItem(selected.dataset.value);
  } else if (e.key === 'Escape') {
    hideAutocomplete();
  }
}

